import React, {Component, Fragment} from 'react';
import logo from './logo.svg';
import './App.css';
import L from 'leaflet';
import Select from 'react-select';
import Draggable from 'react-draggable';
import { Card, CardImg, CardBody, Button, CardTitle, CardText, Form, FormGroup, Label, Input, FormText, Col, Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  ButtonDropdown,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem } from 'reactstrap';
import { Map, TileLayer, Marker, Popup, LayersControl, ZoomControl } from 'react-leaflet';
//import {BananaMarkers} from "./BananaMarkers";

import { MDBDataTable, MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, 
        MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter, MDBTable, 
        MDBTableBody, MDBTableHead
        } from "mdbreact";
import Tabletop from 'tabletop';
import {Modal, Glyphicon} from 'react-bootstrap';
import FontAwesome from 'react-fontawesome';

type Position = [number, number];

type Props = {|
  content: string,
  position: Position,
  icon: bananaIcon
|};

type MarkerData = {| ...Props, key: string |};

const MyPopupMarker = ({ content, position, icon }: Props) => (
  <Marker position={position} icon={icon}>
    <Popup>{content}</Popup>
  </Marker>
);

const MyMarkersList = ({ markers }: { markers: Array<MarkerData> }) => {
  const items = markers.map(({ key, ...props }) => (
    <MyPopupMarker key={key} {...props} />
  ))
  return <Fragment>{items}</Fragment>
};

type State = {
  markers: Array<MarkerData>,
};



const { BaseLayer, Overlay } = LayersControl;


const options = [
  { value: 'banana', label: 'Banana' },
  { value: 'cacao', label: 'Cacao' },
  { value: 'corn', label: 'Corn' },
  { value: 'rice', label: 'Rice' },
  { value: 'soya', label: 'Soya' },
  { value: 'sugarcane', label: 'Sugarcane' },
  { value: 'tomato', label: 'Tomato' },
];

const province = [
  { value: 'laguna', 
  label: 'Laguna' },
];

const municipality = [
  { value: 'victoria', label: 'Victoria' },
  { value: 'los baÃ±os', label: 'Los BaÃ±os' },
  { value: 'jones', label: 'Jones' },
];

var cacaoIcon = L.icon({
    iconUrl: 'cacaomarker.png',
    iconSize: [55, 55],
    iconAnchor: [12.5, 41],
    popupAnchor: [0, -41], 
    shadowUrl: 'my-icon-shadow.png',
    shadowSize: [68, 95],
});

var bananaIcon = L.icon({
    iconUrl: 'bananamarker.png',
    iconSize: [55, 55],
    iconAnchor: [12.5, 41],
    popupAnchor: [0, -41], 
    shadowUrl: 'my-icon-shadow.png',
    shadowSize: [68, 95],
});

var cornIcon = L.icon({
    iconUrl: 'cornmarker.png',
    iconSize: [55, 55],
    iconAnchor: [12.5, 41],
    popupAnchor: [0, -41], 
    shadowUrl: 'my-icon-shadow.png',
    shadowSize: [68, 95],
});

var riceIcon = L.icon({
    iconUrl: 'ricemarker.png',
    iconSize: [55, 55],
    iconAnchor: [12.5, 41],
    popupAnchor: [0, -41], 
    shadowUrl: 'my-icon-shadow.png',
    shadowSize: [68, 95],
});

var soyIcon = L.icon({
    iconUrl: 'soybeanmarker.png',
    iconSize: [55, 55],
    iconAnchor: [12.5, 41],
    popupAnchor: [0, -41], 
    shadowUrl: 'my-icon-shadow.png',
    shadowSize: [68, 95],
});

var sugarCaneIcon = L.icon({
    iconUrl: 'sugarcanemarker.png',
    iconSize: [55, 55],
    iconAnchor: [12.5, 41],
    popupAnchor: [0, -41], 
    shadowUrl: 'my-icon-shadow.png',
    shadowSize: [68, 95],
});

var tomatoIcon = L.icon({
    iconUrl: 'tomatomarker.png',
    iconSize: [55, 55],
    iconAnchor: [12.5, 41],
    popupAnchor: [0, -41], 
    shadowUrl: 'my-icon-shadow.png',
    shadowSize: [68, 95],
});

const bananaID = '1UEUrcBZWiPYr3f0sOEpcaS5SjpaFO1Dm7e2l47uOaRg';
const cornID = '1TGbBxufMVRBCGUpNvOTZN9cV6qOGiQaP3FiQD1187FA';
const cacaoID = '1OfAOQjibxGUTyPPAcgQ7tQX1rx_f2zsXK_MANy97Hq0';
const riceID = '1ytQ4eaRntQakcKyStP7_i1oAWjgqpTCimOY_HZ_CGrE';
const soybeanID = '1e4uZzHWHwTsKuR13Sc2Ezh_eA0ecvHKTNeQCgCHfl2Q';
const tomatoID = '1D9TS3yKL6laJCEGgD0zTMGVns0FRjQ3q2mEd_t91fNU';
const sugarCaneID = '1KXrAjUrXJ2oYCd7gC5Pl06Z5gIhVxjIwxyuMT_Nsezw';




class App extends Component{
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.toggleCard = this.toggleCard.bind(this);
    this.toggleModal = this.toggleModal.bind(this);
    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.changeIcon = this.changeIcon.bind(this);
    this.toggleSampleModal = this.toggleSampleModal.bind(this);
    this.state = { 
      location: {
        lat: 12.8797,
        lng: 121.7740,
      },
      haveUsersLocation: false,
      zoom: 13,
      userMessage: {
        name: '',
        message: '',
      },
      collapse: false,
      collapseCard: true,
      dropdownOpen: false,
      selectedOption: null,
      selectedProvince: null,
      selectedMunicipality: null,
      dataSet: [],
      tableRows: [],
      modal: false,
      show: false,
      modalData: [],
      crop: null,
      pH: null,
      om: null,
      nitrogen: null,
      phosphorus: null,
      pAnalysis: null,
      potassium: null,
      texturalGrade: null,
      remarks: null,
      collaborator: null,
      barangay: null,
      municipality: null,
      province: null,
      latitude: null,
      longitude: null,
      dateSampled: null,
      soilProfile: null,
      provinceList: [],
      markers: [],
      googleSheetID: null,
    };

  }


  componentDidUpdate() {
    navigator.geolocation.getCurrentPosition((position) =>  {
      this.setState({
        location: {
          lat: position.coords.latitude,
          lng: position.coords.longitude 
        },
        haveUsersLocation: true,
        zoom: 13,
      });
    }, () => {
      console.log('Failure to fetch location');
      fetch('https://ipapi.co/json')
        .then(res => res.json())
        .then(location => {
          console.log(location);
        });
    });

    Tabletop.init({
      key: this.state.googleSheetID,
      callback: googleData => {
        console.log(googleData)
        this.setState({
          tableRows: googleData.map((post) => {
            return (
              { 
                button: <div>

                        <div>

                        <button id = "button_toggle" onClick={() => this.toggleModal(post)} >
                        <FontAwesome
                          className='fas fa-plus'
                        />
                        </button>
                        </div>
                        </div>
                        ,
                crop: post.crop,
                pH: post.pH,
                om: post.om,
                nitrogen: post.nitrogen,
                phosphorus: post.phosphorus,
                pAnalysis: post.pAnalysis,
                potassium: post.potassium,
              }
            )
          }),
          provinceList: googleData.map((post) => {
            return (

                { 
                value: post.province, label: post.province 
                }

            )
          }),
          markers: googleData.map((post) => {
            return (
              {
                key: post.id,
                position: [post.latitude, post.longitude],
                content:

                        <div>
                        Province: {post.province}
                        <br /> 
                        Crop: {post.crop}
                        <br />
                        pH: {post.pH}
                        <br/> 
                        <button onClick={() => this.toggleModal(post)} >
                        See more
                        </button>
                        </div>
                        ,
                  icon: this.changeIcon(post),

              }
            )
          }),
        })
      },
      simpleSheet: true
    })
  }



  formSubmitted = (event) => {
    event.preventDefault();
    console.log(this.state.userMessage)
  }

  valueChanged = (event) => {
    const { name, value } = event.target;
    this.setState((prevState) => ({
       userMessage: {
          ...prevState.userMessage,
          [name]: value
       }
    }))
  }

  toggle() {
    this.setState(state => ({ collapse: !state.collapse }));
  }

  handleShow() {
    this.setState({ show: true });
  }

  changeIcon(post) {
    var cropIcon;
    if (post.crop === "Banana"){
      cropIcon = bananaIcon;
    }
    else if (post.crop === "Cacao"){
